    private void exitButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_exitButtonActionPerformed
        int confirmed = JOptionPane.showConfirmDialog(this, "Are you sure you want to exit the application?", "Confirm Exit", JOptionPane.YES_NO_OPTION);

        if (confirmed == JOptionPane.YES_OPTION) {
            JOptionPane.showMessageDialog(this, "Thank you for banking with Bountiful Bank.", "Exiting Bank", JOptionPane.PLAIN_MESSAGE);
            System.exit(0);
        }
    }//GEN-LAST:event_exitButtonActionPerformed

