    private void depositButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_depositButtonActionPerformed
        String depositInput = depositAmountTextField.getText().trim();
        if (depositInput.isEmpty()){
            JOptionPane.showMessageDialog(this, "Field should not be empty.", "Empty field", JOptionPane.ERROR_MESSAGE);
        }
        else if (!depositInput.matches("\\d*\\.?\\d+") || Double.parseDouble(depositInput) <= 0) {
            JOptionPane.showMessageDialog(this, "Enter positive digits only", "Invalid input", JOptionPane.ERROR_MESSAGE);
        }
        else {
            double depositAmount = Double.parseDouble(depositInput);

            int confirm = JOptionPane.showConfirmDialog(this, "Are you sure you want to deposit " + depositAmount + " PHP?", 
                    "Confirm Deposit", JOptionPane.YES_NO_OPTION);

            if (confirm == JOptionPane.YES_OPTION) {
                balance += depositAmount;
                JOptionPane.showMessageDialog(this, "Successfully deposited "+depositAmount+" PHP"+ "\nBalance: "+ balance+" PHP", "Transaction Successful", 
                        JOptionPane.PLAIN_MESSAGE);

                depositAmountTextField.setText("");
                menuTabbedPane.setSelectedIndex(0);
            }
        }
    }//GEN-LAST:event_depositButtonActionPerformed

