    private void withdrawButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_withdrawButtonActionPerformed
        String withdrawInput = withdrawAmountTextField.getText().trim();

        if (withdrawInput.isEmpty()){
            JOptionPane.showMessageDialog(this, "Field should not be empty.", "Empty field", JOptionPane.ERROR_MESSAGE);
        }
        else if (!withdrawInput.matches("\\d*\\.?\\d+") || Double.parseDouble(withdrawInput) <= 0) {
            JOptionPane.showMessageDialog(this, "Enter positive digits only", "Invalid input", JOptionPane.ERROR_MESSAGE);
        }
        else {
            double withdrawAmount = Double.parseDouble(withdrawInput);
            if (balance - withdrawAmount < 100) {
                JOptionPane.showMessageDialog(this, "There must be at least 100 PHP remaining balance after withdrawal",
                        "Invalid amount", JOptionPane.ERROR_MESSAGE);
            }
            else {
                int confirm = JOptionPane.showConfirmDialog(this, "Are you sure you want to withdraw " + withdrawAmount + " PHP?", 
                        "Confirm Deposit", JOptionPane.YES_NO_OPTION);

                if (confirm == JOptionPane.YES_OPTION) {
                    balance -= withdrawAmount;
                    JOptionPane.showMessageDialog(this, "Successfully withdrawn "+withdrawAmount+" PHP" + "\nBalance: "+ balance+" PHP", "Transaction Successful", 
                            JOptionPane.PLAIN_MESSAGE);
                    
                withdrawAmountTextField.setText("");
                menuTabbedPane.setSelectedIndex(0);
                }
            }
        }

    }//GEN-LAST:event_withdrawButtonActionPerformed

