    private void transferButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_transferButtonActionPerformed
        String transferAmountInput = transferAmountTextField.getText().trim();
        String recipientAccountNumber = recipientAccountNumberTextField.getText().trim();
        String currentAccountNumber = "100001"; // Assuming this is the current account number

        if (transferAmountInput.isEmpty() || recipientAccountNumber.isEmpty()){
            JOptionPane.showMessageDialog(this, "Fields should not be empty.", "Empty fields", JOptionPane.ERROR_MESSAGE);
        }
        else if (!transferAmountInput.matches("\\d*\\.?\\d+") || Double.parseDouble(transferAmountInput) <= 0) {
            JOptionPane.showMessageDialog(this, "Enter positive digits only for transfer amount", "Invalid input", JOptionPane.ERROR_MESSAGE);
        }
        else if (!recipientAccountNumber.matches("\\d{6}")) {
            JOptionPane.showMessageDialog(this, "Recipient account number should be a 6-digit number", "Invalid input", JOptionPane.ERROR_MESSAGE);
        }
        else if (recipientAccountNumber.equals(currentAccountNumber)) {
            JOptionPane.showMessageDialog(this, "Cannot transfer funds to your own account", "Invalid input", JOptionPane.ERROR_MESSAGE);
        }
        else {
            double transferAmount = Double.parseDouble(transferAmountInput);
            if (balance - transferAmount < 100) {
                JOptionPane.showMessageDialog(this, "There must be at least 100 PHP remaining balance after transfer", "Insufficient balance", JOptionPane.ERROR_MESSAGE);
            }
            else {
                int confirm = JOptionPane.showConfirmDialog(this, "Are you sure you want to transfer " + transferAmount + " PHP to account number " + recipientAccountNumber + "?", 
                        "Confirm Transfer", JOptionPane.YES_NO_OPTION);

                if (confirm == JOptionPane.YES_OPTION) {
                    // Perform transfer logic here, deducting from sender's balance and crediting recipient's account
                    balance -= transferAmount;
                    // Example: recipientBalance += transferAmount;

                    JOptionPane.showMessageDialog(this, "Successfully transferred "+ transferAmount +" PHP to \nRecipient Account Number: "+ recipientAccountNumber +"\nBalance: "+ balance +" PHP", "Transaction Successful", 
                            JOptionPane.PLAIN_MESSAGE);
                    
                    transferAmountTextField.setText("");
                    recipientAccountNumberTextField.setText("");
                    menuTabbedPane.setSelectedIndex(0);
                }
            }
        }
    }//GEN-LAST:event_transferButtonActionPerformed

